import React, {useState} from "react";



interface Product {
    id: number,
    name: string,
    weight: number,
    energy: number,
    proteins: number,
    carbohydrates: number,
    fats: number
  }

const SearchBar = () => {
  const [input, setInput] = useState("");
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [results, setResults] = useState<Product[]>([]);


  const fetchData = (value:string) => {
    fetch("http://localhost:8000/api/products/get")
      .then((response) => response.json())
      .then((json) => {
        const results = json.filter((product:Product) => {
          return (
            value &&
            product &&
            product.name &&
            product.name.toLowerCase().includes(value)
          );
        });
        setResults(results);
      });
  };

  const handleChange = (value:string) => {
    setInput(value);
    fetchData(value);
  };

  const clearSelectedProduct = () => {
    setSelectedProduct(null);
  };

  const handleProductClick = (product: Product) => {
    setSelectedProduct(product);
  };

  return (
    <div>
        <input
          placeholder="Szukaj"
          value={input}
          onChange={(e) => handleChange(e.target.value)}
        />
        <ul>
        {results.map((product) => (
          <li key={product.id} onClick={() => handleProductClick(product)}>
            {product.name}
          </li>
        ))}
      </ul>

      {selectedProduct && (
        <div>
          <p>{selectedProduct.name}</p>
          <p>Waga: {selectedProduct.weight} gram</p>
          <p>Energy: {selectedProduct.energy}</p>
          <p>Proteins: {selectedProduct.proteins}</p>
          <p>Carbohydrates: {selectedProduct.carbohydrates}</p>
          <p>Fats: {selectedProduct.fats}</p>
          <button onClick={clearSelectedProduct}>Clear Selection</button>
        </div>
      )}
    </div>
  );
};

export default SearchBar;
